<?php
session_start(); 

if (!isset($_SESSION['nama_kasir'])) {
    echo "Kasir belum login, silakan login terlebih dahulu!";
    exit;
}

$nama_kasir = strtolower($_SESSION['nama_kasir']);
$foto_kasir = "/website/purple-free/dist/assets/images/faces/" . $nama_kasir . ".jpeg";
include '../../../../config/koneksi.php';

if ($_SERVER['REQUEST_METHOD'] === 'POST') {
    $nama = $_POST['nama_menu'];
    $harga = $_POST['harga'];
    $kategori = $_POST['kategori'];
    $deskripsi = $_POST['deskripsi'];
    $stok = $_POST['stok'];

    $gambar = '';
    if ($_FILES['gambar']['error'] === 0) {
        $gambar = basename($_FILES['gambar']['name']);
        $targetDir = "../../../assets/img/";
        $targetFile = $targetDir . $gambar;
        move_uploaded_file($_FILES['gambar']['tmp_name'], $targetFile);
    }

    $query = "INSERT INTO menu (nama_menu, harga, kategori, deskripsi, gambar, stok)
              VALUES ('$nama', '$harga', '$kategori', '$deskripsi', '$gambar', '$stok')";

    if (mysqli_query($conn, $query)) {
      header("Location: ../../pages/tables/basic-table-daftarmenu.php");
      exit;
    } else {
        echo "Gagal menambahkan menu: " . mysqli_error($conn);
    }
}
?>

