$foto_kasir = "/website/purple-free/dist/assets/images/faces/" . $nama_kasir . ".jpeg";

include '../../../../config/koneksi.php';

if (isset($_POST['simpan'])) {
  $kode = $_POST['kode'];
  $jenis = $_POST['jenis'];
  $nilai = $_POST['nilai'];
  $aktif = $_POST['aktif'];
  $tanggal_mulai = $_POST['tanggal_mulai'];
  $tanggal_akhir = $_POST['tanggal_akhir'];

  $query = "INSERT INTO kode_promo (kode, jenis, nilai, aktif, tanggal_mulai, tanggal_akhir)
            VALUES ('$kode', '$jenis', '$nilai', '$aktif', '$tanggal_mulai', '$tanggal_akhir')";
  mysqli_query($conn, $query);

  header("Location: ../../pages/tables/diskon.php");
  exit;
}
